import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import ModelsCard from './ModelsCard';
import modelsData from './models.json';
import './Models.css';

const Models = ({ onSelectModel }) => {
  const { categoryCode, manufacturerCode, seriesId } = useParams();
  const [selectedModel, setSelectedModel] = useState(null);

  const filteredModels = modelsData.models.filter(
    (model) => model.seriesId === parseInt(seriesId)
  );

  const handleSelectModel = (model) => {
    setSelectedModel(model);
    onSelectModel(categoryCode, manufacturerCode, seriesId, model.id);
  };

  return (
    <div className="models-container">
      <div className="grid-container">
        {filteredModels.map(model => (
          <ModelsCard
            key={model.id}
            model={model}
            isActive={selectedModel && selectedModel.id === model.id}
            onSelect={() => handleSelectModel(model)}
          />
        ))}
      </div>
    </div>
  );
};

export default Models;
