import React from 'react';
import { useParams } from 'react-router-dom';
import SeriesCard from './SeriesCard';
import seriesData from './series.json';
import './Series.css';

const Series = ({ onSelectSeries }) => {
  const { categoryCode, manufacturerCode } = useParams();

  const filteredSeries = seriesData.series.filter(
    (seriesItem) => 
      seriesItem.categoryId === parseInt(categoryCode) && 
      seriesItem.manufacturerId === parseInt(manufacturerCode)
  );

  return (
    <div className="grid-container">
      {filteredSeries.length > 0 ? (
        filteredSeries.map(seriesItem => (
          <SeriesCard
            key={seriesItem.id}
            series={seriesItem}
            onSelect={() => onSelectSeries(categoryCode, manufacturerCode, seriesItem.id)}
          />
        ))
      ) : (
        <div>No se encontraron series para la categor√≠a y fabricante seleccionados.</div>
      )}
    </div>
  );
};

export default Series;
