import React, { useEffect, useRef } from 'react';
import './CategoryCard.css';

const CategoryCard = ({ category, onSelect }) => {
  const svgRef = useRef(null);

  useEffect(() => {
    fetch(`/assets/icons/${category.icon}`)
      .then(response => response.text())
      .then(svg => {
        if (svgRef.current) {
          svgRef.current.innerHTML = svg;
        }
      })
      .catch(error => console.error('Error loading SVG:', error));
  }, [category.icon]);

  return (
    <div className='col-sm-12 col-md-6 col-lg-4'>
      <div className={`card mb-3 category ${category.code}`} onClick={() => onSelect(category)}>
        <div className="row g-0">
          <div className="col-sm-2 d-flex justify-content-center align-items-center p-3">
            <div ref={svgRef} className="svg-container"></div>
          </div>
          <div className="col-sm-10 d-flex align-items-center">
            <div className="ps-0 card-body">
              <h5 className="card-title m-0">{category.name}</h5>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CategoryCard;
