import React from 'react';
import { IS_LOCAL } from '../../config';
import './ItemCard.css';

const ItemCard = ({ item, onSelect }) => {
  const icon = IS_LOCAL ? item.icon_local : item.icon;  // Selección del icono basado en el entorno

  return (
    <div className='col-12 col-md-6 col-lg-4 m-0 p-2'>
      <div className={`card item`} onClick={() => onSelect(item)}>
        <div className="row g-0">
          <div className="col-2 d-flex justify-content-center align-items-center p-3">
            <img src={icon} alt={item.name} className="icon-img" />
          </div>
          <div className="col-10 d-flex align-items-center">
            <div className="ps-0 card-body">
              <h5 className="card-title m-0">{item.name}</h5>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ItemCard;
