import React from 'react';
import './CategoryCard.css';

const CategoryCard = ({ category, onSelect }) => {
  // Construye la ruta de la imagen usando require
  const iconPath = require(`assets/icons/${category.icon}`).default;

  return (
    <div className='col'>
      <div className={`card mb-3 ${category.code}`} onClick={() => onSelect(category)}>
        <div className="row">
          <div className="col-4">
            <img src={iconPath} alt={category.name} className="img-fluid rounded-start" />
          </div>
          <div className="col-8">
            <div className="card-body">
              <h5 className="card-title">{category.name}</h5>
              <p className="card-text">This is a wider card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.</p>
              <p className="card-text"><small className="text-body-secondary">Last updated 3 mins ago</small></p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CategoryCard;
