import React from 'react';
import SelectionList from '../shared/SelectionList';
import manufacturersData from './manufacturers.json';
import categoriesData from '../categories/categories.json';

const Manufacturer = ({ onSelectManufacturer }) => {
  const categoryCode = new URLSearchParams(window.location.search).get('category');

  // Filtrar los fabricantes que pertenecen a la categoría seleccionada
  const filteredManufacturers = manufacturersData.manufacturers.filter(
    manufacturer => manufacturer.categories.some(cat => cat.code === categoryCode && cat.active)
  );

  return (
    <SelectionList
      data={filteredManufacturers}
      onSelect={(manufacturer) => onSelectManufacturer(categoryCode, manufacturer)}
      title="Manufacturer"
    />
  );
};

export default Manufacturer;
