import React from 'react';
import './CategoryCard.css';

const CategoryCard = ({ category, onSelect }) => {
  return (
    <div className="col-12 col-md-6 col-lg-4 m-0 p-2">
      <div className="card category" onClick={onSelect}>
        <div className="row g-0">
          <div className="col-12 d-flex align-items-center">
            <div className="card-body">
              <img src={`${category.icon}`} alt={category.name} className="img-fluid me-2" style={{ height: '50px' }} />
              <h5 className="card-title m-0">{category.name}</h5>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CategoryCard;
