import React from 'react';
import SelectionList from '../shared/SelectionList';
import modelsData from './models.json';
import seriesData from '../series/series.json';
import manufacturersData from '../manufacturers/manufacturers.json';

const Models = ({ onSelectModel }) => {
  const params = new URLSearchParams(window.location.search);
  const seriesId = parseInt(params.get('series'));
  const series = seriesData.series.find(s => s.id === seriesId);
  const manufacturer = manufacturersData.manufacturers.find(m => m.id === series?.manufacturerId);

  const filteredModels = modelsData.models.filter(model => model.seriesId === seriesId);

  return (
    <div className="container-fluid mb-5">
      <div className="row">
        <div className="d-flex align-items-center">
          <img src={manufacturer.logo} alt={manufacturer.name} className="img-fluid me-2 border" style={{ height: '100px' }} />
          <h4 className='ps-2'>{manufacturer.name} <span className='fw-bold'>Models</span></h4>
        </div>
      </div>
      <SelectionList
        data={filteredModels}
        onSelect={(model) => onSelectModel(model)}
        title="Model"
      />
    </div>
  );
};

export default Models;
