import React, { useState } from 'react';
import './ModelDetails.css';

const ModelDetails = ({ model, onDeselect }) => {
  const [selectedAttributes, setSelectedAttributes] = useState({});

  const handleSelectAttribute = (attribute, value) => {
    setSelectedAttributes((prevSelected) => {
      const updatedSelected = { ...prevSelected };
      if (updatedSelected[attribute] === value) {
        delete updatedSelected[attribute];
      } else {
        updatedSelected[attribute] = value;
      }
      return updatedSelected;
    });
  };

  const attributes = Object.keys(model.additionalAttributes);

  return (
    <div className="model-details">
      <h3>{model.name}</h3>
      <p>Code: {model.sku}</p>
      <p>{model.description}</p>
      {attributes.length > 0 && (
        <>
          {attributes.map((attribute) => (
            <div key={attribute} className="attribute-section">
              <h4>{attribute}</h4>
              <div className="options">
                {model.additionalAttributes[attribute].map((value) => (
                  <div
                    key={value}
                    className={`option ${selectedAttributes[attribute] === value ? 'selected' : ''}`}
                    onClick={() => handleSelectAttribute(attribute, value)}
                  >
                    {value}
                  </div>
                ))}
              </div>
            </div>
          ))}
        </>
      )}
      <button onClick={onDeselect}>Back</button>
    </div>
  );
};

export default ModelDetails;
