import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import SkuSearch from '../modules/sku/SkuSearch';
import './Home.css';

const Home = ({ onSelectCategory }) => {
  const navigate = useNavigate();


  useEffect(() => {
    localStorage.removeItem('appState');
  }, []);

  const handleCategoryClick = () => {
    onSelectCategory();
  };

  return (
    <div className="container-fluid my-5 py-5">
      <div className="row">
        <div className='col-6'>
          <h3>Guided search</h3>
          <p>We will guide you to find your current product and show you the best alternatives to improve your pool</p>
          <p><small>Let's start by selecting the category of the product you want to search for.</small></p>
          <button className="btn btn-primary px-5 py-3 rounded-pill" onClick={handleCategoryClick}>Select Category</button>
        </div>
        <div className='col-6'>
          <SkuSearch />
        </div>
      </div>
    </div>
  );
};

export default Home;
