import React from 'react';
import './CategoryCard.css';

const CategoryCard = ({ category, onSelect }) => {
  return (
    <div className='col-sm-12 col-md-6 col-lg-4'>
      <div className={`card mb-3 ${category.code}`} onClick={() => onSelect(category)}>
        <div className="row g-0">
          <div className="col-sm-2 d-flex justify-content-center align-items-center p-3">
            <img src={category.icon} alt={category.name} className="img-fluid rounded-start" />
          </div>
          <div className="col-sm-10">
            <div className="ps-0 card-body d-flex justify-content-center">
              <h5 className="card-title">{category.name}</h5>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CategoryCard;
