import React, { useEffect } from 'react';
import SelectionList from '../shared/SelectionList';
import seriesData from './series.json';
import manufacturersData from '../manufacturers/manufacturers.json';
import { useNavigate } from 'react-router-dom';
import { IS_LOCAL } from '../../config';

const Series = ({
  onSelectSeries,
  setManufacturer,
  setManufacturerName,
  setManufacturerLogo,
  setSeries,
  setSeriesName,
  setModel,
  setModelName
}) => {
  const params = new URLSearchParams(window.location.search);
  const manufacturerCode = params.get('manufacturer');
  const navigate = useNavigate();

  const manufacturer = manufacturersData.manufacturers.find(manufacturer => manufacturer.code === manufacturerCode);
  const filteredSeries = seriesData.series.filter(seriesItem => seriesItem.manufacturerId === manufacturer.id);

  useEffect(() => {
    if (manufacturer) {
      setManufacturerLogo(IS_LOCAL ? manufacturer.logo_local : manufacturer.logo);  // Seleccionar logo según el entorno
      setManufacturerName(manufacturer.name);
    }
  }, [manufacturer, setManufacturerLogo, setManufacturerName]);

  const handleSelectSeries = (categoryCode, manufacturerCode, seriesId) => {
    const series = seriesData.series.find(series => series.id === parseInt(seriesId));

    if (!series) {
      console.error("Series not found");
      return;
    }

    setSeries(series.id);
    setSeriesName(series.name);
    setModel('');
    setModelName('Select One');
    navigate(`/?tab=model&category=${categoryCode}&manufacturer=${manufacturerCode}&series=${seriesId}`);
  };

  return (
    <div className="container-fluid mb-5">
      <div className="row">
        <div className="d-flex align-items-center">
          <img src={IS_LOCAL ? manufacturer?.logo_local : manufacturer?.logo} alt={manufacturer?.name} className="img-fluid me-2 border" style={{ height: '100px' }} />
          <h4 className='ps-2'>{manufacturer?.name} <span className='fw-bold'>Series</span></h4>
        </div>
      </div>
      <SelectionList
        data={filteredSeries}
        onSelect={(seriesItem) => handleSelectSeries(params.get('category'), manufacturerCode, seriesItem.id)}
        title={null}  // Eliminar el título "Select Series"
      />
    </div>
  );
};

export default Series;
