import React from 'react';
import { Link } from 'react-router-dom';
import './TabNav.css';

const TabNav = ({ activeTab, category, manufacturer, series, model, categoryName, manufacturerName, seriesName, modelName, setActiveTab }) => {
  const handleTabClick = (tab, path) => {
    if (tab) {
      setActiveTab(tab);
    }
  };

  return (
    <div className="tab-nav">
      <div className={`tab-item ${activeTab === 'category' ? 'active' : ''}`} onClick={() => handleTabClick('category', '/')}>
        <Link to="/">Category</Link>
        {categoryName && <div className="subtitle">{categoryName}</div>}
      </div>
      <div className={`tab-item ${activeTab === 'manufacturer' ? 'active' : ''} ${!category ? 'disabled' : ''}`} onClick={() => category && handleTabClick('manufacturer', `/${category}`)}>
        <Link to={`/${category}`}>Manufacturer</Link>
        {manufacturerName && <div className="subtitle">{manufacturerName}</div>}
      </div>
      <div className={`tab-item ${activeTab === 'series' ? 'active' : ''} ${!manufacturer ? 'disabled' : ''}`} onClick={() => manufacturer && handleTabClick('series', `/${category}/${manufacturer}`)}>
        <Link to={`/${category}/${manufacturer}`}>Series</Link>
        {seriesName && <div className="subtitle">{seriesName}</div>}
      </div>
      <div className={`tab-item ${activeTab === 'model' ? 'active' : ''} ${!series ? 'disabled' : ''}`} onClick={() => series && handleTabClick('model', `/${category}/${manufacturer}/${series}`)}>
        <Link to={`/${category}/${manufacturer}/${series}`}>Model</Link>
        {modelName && <div className="subtitle">{modelName}</div>}
      </div>
      <div className={`tab-item ${activeTab === 'alternative' ? 'active' : ''} ${!model ? 'disabled' : ''}`} onClick={() => model && handleTabClick('alternative', `/${category}/${manufacturer}/${series}/${model}/alternatives`)}>
        <Link to={`/${category}/${manufacturer}/${series}/${model}/alternatives`}>Alternative</Link>
      </div>
    </div>
  );
};

export default TabNav;
