import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import modelsData from './models.json';
import './Alternatives.css';

const Alternatives = () => {
  const { categoryCode, manufacturerCode, seriesId, modelId } = useParams();
  const navigate = useNavigate();

  const selectedModel = modelsData.models.find(model => model.id === parseInt(modelId));

  if (!selectedModel || !selectedModel.relatedModels) {
    return <div>No se encontraron alternativas para este modelo.</div>;
  }

  const goodModel = modelsData.models.find(model => model.id === selectedModel.relatedModels.good);
  const betterModel = modelsData.models.find(model => model.id === selectedModel.relatedModels.better);
  const bestModel = modelsData.models.find(model => model.id === selectedModel.relatedModels.best);

  const handleStartAgain = () => {
    navigate('/');
  };

  return (
    <div className="alternatives-container">
      <h2>HAYWARD Alternatives</h2>
      <div className="flex-container">
        <div className="model-card good">
          <h3>Good</h3>
          {goodModel ? (
            <>
              <img src={`/images/${goodModel.image}`} alt={goodModel.name} />
              <h4>{goodModel.name}</h4>
              <p>SKU: {goodModel.sku}</p>
              <p>{goodModel.description}</p>
              <a href={goodModel.productDetailUrl} target="_blank" rel="noopener noreferrer">View Details</a>
            </>
          ) : (
            <p>No good alternative available</p>
          )}
        </div>
        <div className="model-card best">
          <h3>Best</h3>
          {bestModel ? (
            <>
              <img src={`/images/${bestModel.image}`} alt={bestModel.name} />
              <h4>{bestModel.name}</h4>
              <p>SKU: {bestModel.sku}</p>
              <p>{bestModel.description}</p>
              <a href={bestModel.productDetailUrl} target="_blank" rel="noopener noreferrer">View Details</a>
            </>
          ) : (
            <p>No best alternative available</p>
          )}
        </div>
        <div className="model-card better">
          <h3>Better</h3>
          {betterModel ? (
            <>
              <img src={`/images/${betterModel.image}`} alt={betterModel.name} />
              <h4>{betterModel.name}</h4>
              <p>SKU: {betterModel.sku}</p>
              <p>{betterModel.description}</p>
              <a href={betterModel.productDetailUrl} target="_blank" rel="noopener noreferrer">View Details</a>
            </>
          ) : (
            <p>No better alternative available</p>
          )}
        </div>
      </div>
      <button onClick={handleStartAgain}>Start Again</button>
    </div>
  );
};

export default Alternatives;
