import React from 'react';
import SelectionList from '../shared/SelectionList';
import seriesData from './series.json';
import categoriesData from '../categories/categories.json';
import manufacturersData from '../manufacturers/manufacturers.json';

const Series = ({ onSelectSeries }) => {
  const params = new URLSearchParams(window.location.search);
  const categoryCode = params.get('category');
  const manufacturerCode = params.get('manufacturer');

  const categoryId = categoriesData.categories.find(category => category.code === categoryCode)?.id;
  const manufacturer = manufacturersData.manufacturers.find(manufacturer => manufacturer.code === manufacturerCode);

  const filteredSeries = seriesData.series.filter(
    seriesItem => seriesItem.categoryId === categoryId && seriesItem.manufacturerId === manufacturer.id
  );

  return (
    <div className="container-fluid mb-5">
      <div className="row">
        <div className="d-flex align-items-center">
          <img src={manufacturer.logo} alt={manufacturer.name} className="img-fluid me-2 border" style={{ height: '100px' }} />
          <h4 className='ps-2'>{manufacturer.name} <span className='fw-bold'>Series</span></h4>
        </div>
      </div>
      <SelectionList
        data={filteredSeries}
        onSelect={(seriesItem) => onSelectSeries(categoryCode, manufacturerCode, seriesItem.id)}
        title="Series"
      />
    </div>
  );
};

export default Series;
