import React from 'react';
import './TabNav.css';

const TabNav = ({ activeTab, category, manufacturer, series, model, categoryName, manufacturerName, seriesName, modelName, setActiveTab, navigate }) => {
  const handleTabClick = (tab, path) => {
    if (tab) {
      setActiveTab(tab);
      navigate(path);
    }
  };

  return (
    <div className="tab-nav">
      <div
        className={`tab-item ${activeTab === 'category' ? 'active' : ''}`}
        onClick={() => handleTabClick('category', '/')}
      >
        <div>Category</div>
        {categoryName && <div className="subtitle">{categoryName}</div>}
      </div>
      <div
        className={`tab-item ${activeTab === 'manufacturer' ? 'active' : ''} ${!category ? 'disabled' : ''}`}
        onClick={() => category && handleTabClick('manufacturer', `/${category}`)}
      >
        <div>Manufacturer</div>
        {manufacturerName && <div className="subtitle">{manufacturerName}</div>}
      </div>
      <div
        className={`tab-item ${activeTab === 'series' ? 'active' : ''} ${!manufacturer ? 'disabled' : ''}`}
        onClick={() => manufacturer && handleTabClick('series', `/${category}/${manufacturer}`)}
      >
        <div>Series</div>
        {seriesName && <div className="subtitle">{seriesName}</div>}
      </div>
      <div
        className={`tab-item ${activeTab === 'model' ? 'active' : ''} ${!series ? 'disabled' : ''}`}
        onClick={() => series && handleTabClick('model', `/${category}/${manufacturer}/${series}`)}
      >
        <div>Model</div>
        {modelName && <div className="subtitle">{modelName}</div>}
      </div>
      <div
        className={`tab-item ${activeTab === 'alternative' ? 'active' : ''} ${!model ? 'disabled' : ''}`}
        onClick={() => model && handleTabClick('alternative', `/${category}/${manufacturer}/${series}/${model}/alternatives`)}
      >
        <div>Alternative</div>
      </div>
    </div>
  );
};

export default TabNav;
