import React from 'react';
import './TabNav.css';

const TabNav = ({ activeTab, categoryName, manufacturerName, seriesName, modelName, setActiveTab }) => {
  const handleTabClick = (tab) => {
    if (tab === 'category') {
      setActiveTab('category');
    } else if (tab === 'manufacturer' && categoryName) {
      setActiveTab('manufacturer');
    } else if (tab === 'series' && manufacturerName) {
      setActiveTab('series');
    } else if (tab === 'model' && seriesName) {
      setActiveTab('model');
    } else if (tab === 'alternative' && modelName) {
      setActiveTab('alternative');
    }
  };

  const getTabClassName = (tab) => {
    return `tab-item ${activeTab === tab ? 'active' : ''} ${!isTabEnabled(tab) ? 'disabled' : ''}`;
  };

  const isTabEnabled = (tab) => {
    if (tab === 'manufacturer') return !!categoryName;
    if (tab === 'series') return !!manufacturerName;
    if (tab === 'model') return !!seriesName;
    if (tab === 'alternative') return !!modelName;
    return true;
  };

  return (
    <div className="tab-nav">
      <div className={getTabClassName('category')} onClick={() => handleTabClick('category')}>
        Category {categoryName && <span className="tab-subtitle">{categoryName}</span>}
      </div>
      <div className={getTabClassName('manufacturer')} onClick={() => handleTabClick('manufacturer')}>
        Manufacturer {manufacturerName && <span className="tab-subtitle">{manufacturerName}</span>}
      </div>
      <div className={getTabClassName('series')} onClick={() => handleTabClick('series')}>
        Series {seriesName && <span className="tab-subtitle">{seriesName}</span>}
      </div>
      <div className={getTabClassName('model')} onClick={() => handleTabClick('model')}>
        Model {modelName && <span className="tab-subtitle">{modelName}</span>}
      </div>
      <div className={getTabClassName('alternative')} onClick={() => handleTabClick('alternative')}>
        Alternative
      </div>
    </div>
  );
};

export default TabNav;
