import React from 'react';
import { useNavigate } from 'react-router-dom';
import './TabNav.css';

const TabNav = ({ activeTab, category, manufacturer, series, model, categoryName, manufacturerName, seriesName, modelName, setActiveTab }) => {
  const navigate = useNavigate();

  const handleTabClick = (tab) => {
    if (tab === 'category') {
      setActiveTab('category');
      navigate('/');
    } else if (tab === 'manufacturer' && category) {
      setActiveTab('manufacturer');
      navigate(`/${category}`);
    } else if (tab === 'series' && manufacturer) {
      setActiveTab('series');
      navigate(`/${category}/${manufacturer}`);
    } else if (tab === 'model' && series) {
      setActiveTab('model');
      navigate(`/${category}/${manufacturer}/${series}`);
    } else if (tab === 'alternative' && model) {
      setActiveTab('alternative');
      navigate(`/${category}/${manufacturer}/${series}/${model}/alternatives`);
    }
  };

  const getTabClassName = (tab) => {
    return `tab-item ${activeTab === tab ? 'active' : ''} ${!isTabEnabled(tab) ? 'disabled' : ''}`;
  };

  const isTabEnabled = (tab) => {
    if (tab === 'manufacturer') return !!category;
    if (tab === 'series') return !!manufacturer;
    if (tab === 'model') return !!series;
    if (tab === 'alternative') return !!model;
    return true;
  };

  return (
    <div className="tab-nav">
      <div className={getTabClassName('category')} onClick={() => handleTabClick('category')}>
        Category {categoryName ? <span className="tab-subtitle">{categoryName}</span> : <span className="tab-subtitle">Select One</span>}
      </div>
      <div className={getTabClassName('manufacturer')} onClick={() => handleTabClick('manufacturer')}>
        Manufacturer {manufacturerName ? <span className="tab-subtitle">{manufacturerName}</span> : <span className="tab-subtitle">Select One</span>}
      </div>
      <div className={getTabClassName('series')} onClick={() => handleTabClick('series')}>
        Series {seriesName ? <span className="tab-subtitle">{seriesName}</span> : <span className="tab-subtitle">Select One</span>}
      </div>
      <div className={getTabClassName('model')} onClick={() => handleTabClick('model')}>
        Model {modelName ? <span className="tab-subtitle">{modelName}</span> : <span className="tab-subtitle">Select One</span>}
      </div>
      <div className={getTabClassName('alternative')} onClick={() => handleTabClick('alternative')}>
        Alternative <span className="tab-subtitle">Select One</span>
      </div>
    </div>
  );
};

export default TabNav;
