import React, { useState } from 'react';
import { Routes, Route, useNavigate } from 'react-router-dom';
import Category from './modules/categories/Category';
import Manufacturer from './modules/manufacturers/Manufacturer';
import Series from './modules/series/Series';
import Models from './modules/models/Models';
import Alternatives from './modules/models/Alternatives';
import TabNav from './components/tabnav/TabNav';
import categoriesData from './modules/categories/categories.json';
import manufacturersData from './modules/manufacturers/manufacturers.json';
import seriesData from './modules/series/series.json';
import modelsData from './modules/models/models.json';
import './App.css';

const Home = ({ onSelectCategory }) => (
  <div className="App">
    <h1>Selecciona la categor√≠a del producto que deseas reemplazar</h1>
    <Category categories={categoriesData.categories} onSelect={onSelectCategory} />
  </div>
);

const App = () => {
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('');
  const [category, setCategory] = useState('');
  const [manufacturer, setManufacturer] = useState('');
  const [series, setSeries] = useState('');
  const [model, setModel] = useState('');
  const [categoryName, setCategoryName] = useState('');
  const [manufacturerName, setManufacturerName] = useState('');
  const [seriesName, setSeriesName] = useState('');
  const [modelName, setModelName] = useState('');

  const handleSelectCategory = (category) => {
    setCategory(category.code);
    setCategoryName(category.name);
    setActiveTab('manufacturer');
    navigate(`/${category.code}`);
  };

  const handleSelectManufacturer = (categoryCode, manufacturerCode) => {
    const manufacturerObj = manufacturersData.manufacturers.find(manufacturer => manufacturer.code === manufacturerCode);
    setManufacturer(manufacturerCode);
    setManufacturerName(manufacturerObj.name);
    setActiveTab('series');
    navigate(`/${categoryCode}/${manufacturerCode}`);
  };

  const handleSelectSeries = (categoryCode, manufacturerCode, seriesId) => {
    const seriesObj = seriesData.series.find(series => series.id === parseInt(seriesId));
    setSeries(seriesId);
    setSeriesName(seriesObj.name);
    setActiveTab('model');
    navigate(`/${categoryCode}/${manufacturerCode}/${seriesId}`);
  };

  const handleSelectModel = (categoryCode, manufacturerCode, seriesId, modelId) => {
    const modelObj = modelsData.models.find(model => model.id === parseInt(modelId));
    setModel(modelId);
    setModelName(modelObj.name);
    setActiveTab('alternative');
    navigate(`/${categoryCode}/${manufacturerCode}/${seriesId}/${modelId}/alternatives`);
  };

  return (
    <div>
      {activeTab && (
        <TabNav
          activeTab={activeTab}
          category={category}
          manufacturer={manufacturer}
          series={series}
          model={model}
          categoryName={categoryName}
          manufacturerName={manufacturerName}
          seriesName={seriesName}
          modelName={modelName}
        />
      )}
      <Routes>
        <Route path="/" element={<Home onSelectCategory={handleSelectCategory} />} />
        <Route path="/:categoryCode" element={<Manufacturer onSelectManufacturer={handleSelectManufacturer} />} />
        <Route path="/:categoryCode/:manufacturerCode" element={<Series onSelectSeries={handleSelectSeries} />} />
        <Route path="/:categoryCode/:manufacturerCode/:seriesId" element={<Models onSelectModel={handleSelectModel} />} />
        <Route path="/:categoryCode/:manufacturerCode/:seriesId/:modelId/alternatives" element={<Alternatives />} />
      </Routes>
    </div>
  );
};

export default App;
