import React, { useEffect } from 'react';
import SelectionList from '../shared/SelectionList';
import modelsData from './models.json';
import seriesData from '../series/series.json';
import manufacturersData from '../manufacturers/manufacturers.json';
import { useNavigate } from 'react-router-dom';
import { IS_LOCAL } from '../../config';

const Models = ({
  onSelectModel,
  setManufacturer,
  setManufacturerName,
  setManufacturerLogo,
  setSeries,
  setSeriesName,
  setModel,
  setModelName
}) => {
  const params = new URLSearchParams(window.location.search);
  const seriesId = parseInt(params.get('series'));
  const series = seriesData.series.find(s => s.id === seriesId);
  const manufacturer = manufacturersData.manufacturers.find(m => m.id === series?.manufacturerId);
  const navigate = useNavigate();

  const filteredModels = modelsData.models.filter(model => model.seriesId === seriesId);

  useEffect(() => {
    if (manufacturer) {
      setManufacturerLogo(IS_LOCAL ? manufacturer.logo_local : manufacturer.logo);  // Seleccionar logo según el entorno
      setManufacturerName(manufacturer.name);
    }
  }, [manufacturer, setManufacturerLogo, setManufacturerName]);

  const handleSelectModel = (model) => {
    setModel(model.id);
    setModelName(model.name);
    navigate(`/?tab=alternative&category=${series?.categoryId}&manufacturer=${manufacturer?.code}&series=${seriesId}&model=${model.id}`);
  };

  return (
    <div className="container-fluid mb-5">
      <div className="row">
        <div className="d-flex align-items-center">
          <img src={IS_LOCAL ? manufacturer?.logo_local : manufacturer?.logo} alt={manufacturer?.name} className="img-fluid me-2 border" style={{ height: '100px' }} />
          <h4 className='ps-2'>{manufacturer?.name} <span className='fw-bold'>Models</span></h4>
        </div>
      </div>
      <SelectionList
        data={filteredModels}
        onSelect={(model) => handleSelectModel(model)}
        title={null}  // Eliminar el título "Select Models"
      />
    </div>
  );
};

export default Models;
