// App.js
import React from 'react';
import { Routes, Route, useNavigate } from 'react-router-dom';
import Category from './modules/categories/Category';
import Manufacturer from './modules/manufacturers/Manufacturer';
import Series from './modules/series/Series';
import Models from './modules/models/Models';
import categoriesData from './modules/categories/categories.json';
import './App.css';

const Home = ({ onSelectCategory }) => (
  <div className="App">
    <h1>Selecciona la categor√≠a del producto que deseas reemplazar</h1>
    <Category categories={categoriesData.categories} onSelect={onSelectCategory} />
  </div>
);

const App = () => {
  const navigate = useNavigate();

  const handleSelectCategory = (category) => {
    navigate(`/${category.code}`);
  };

  const handleSelectManufacturer = (categoryCode, manufacturerCode) => {
    navigate(`/${categoryCode}/${manufacturerCode}`);
  };

  const handleSelectSeries = (categoryCode, manufacturerCode, seriesId) => {
    navigate(`/${categoryCode}/${manufacturerCode}/${seriesId}`);
  };

  return (
    <Routes>
      <Route path="/" element={<Home onSelectCategory={handleSelectCategory} />} />
      <Route path="/:categoryCode" element={<Manufacturer onSelectManufacturer={handleSelectManufacturer} />} />
      <Route path="/:categoryCode/:manufacturerCode" element={<Series onSelectSeries={handleSelectSeries} />} />
      <Route path="/:categoryCode/:manufacturerCode/:seriesId" element={<Models />} />
    </Routes>
  );
};

export default App;
