import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import './Alternatives.css';
import modelsData from './models.json';

const AlternativesAPI = ({ onRestart }) => {
  const { modelId } = useParams();
  const navigate = useNavigate();
  const [relatedModels, setRelatedModels] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const model = modelsData.models.find(model => model.id === parseInt(modelId));

  useEffect(() => {
    if (!model) {
      setLoading(false);
      setError('Model not found');
      return;
    }

    const fetchRelatedModels = async () => {
      try {
        const results = await Promise.all(
          ['good', 'better', 'best'].map(async key => {
            const sku = model.relatedModels[key];
            const response = await fetch(
              `https://hayward.com/rest/default/V1/products/${sku}`,
              {
                headers: {
                  Authorization: "Bearer 0amuph71141a6n2ac2q9p2lhrotcwobi",
                },
              }
            );
            if (!response.ok) {
              throw new Error('Network response was not ok');
            }
            const result = await response.json();
            return result;
          })
        );
        setRelatedModels(results);
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchRelatedModels();
  }, [model]);

  const handleRestart = () => {
    onRestart();
    navigate('/');
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div className="alternatives-container">
      <h2>HAYWARD Alternatives</h2>
      <div className="flex-container">
        {relatedModels.map((relatedModel, index) => (
          <div key={index} className={`model-card ${['good', 'better', 'best'][index]}`}>
            <h3>{['Good', 'Better', 'Best'][index]}</h3>
            <img src={relatedModel.custom_attributes.find(attr => attr.attribute_code === 'image')?.value || ''} alt={relatedModel.name} />
            <h4>{relatedModel.name}</h4>
            <p>SKU: {relatedModel.sku}</p>
            <p dangerouslySetInnerHTML={{ __html: relatedModel.custom_attributes.find(attr => attr.attribute_code === 'description')?.value || '' }}></p>
            <a href={`https://hayward.com/${relatedModel.custom_attributes.find(attr => attr.attribute_code === 'url_key')?.value}`} target="_blank" rel="noopener noreferrer">View Details</a>
          </div>
        ))}
      </div>
      <button onClick={handleRestart}>Start Again</button>
    </div>
  );
};

export default AlternativesAPI;
