import React from 'react';
import './TabNav.css';

const TabNav = ({ activeTab, category, manufacturer, series, model, setActiveTab, onNavigate }) => {
  const handleTabClick = (tab) => {
    if (tab === 'category') {
      setActiveTab('category');
      onNavigate('/');
    } else if (tab === 'manufacturer' && category) {
      setActiveTab('manufacturer');
      onNavigate(`/${category}`);
    } else if (tab === 'series' && category && manufacturer) {
      setActiveTab('series');
      onNavigate(`/${category}/${manufacturer}`);
    } else if (tab === 'model' && category && manufacturer && series) {
      setActiveTab('model');
      onNavigate(`/${category}/${manufacturer}/${series}`);
    } else if (tab === 'alternative' && category && manufacturer && series && model) {
      setActiveTab('alternative');
      onNavigate(`/${category}/${manufacturer}/${series}/${model}/alternatives`);
    }
  };

  return (
    <div className="tab-nav">
      <div className={`tab-item ${activeTab === 'category' ? 'active' : ''}`} onClick={() => handleTabClick('category')}>
        Category {category && <span className="tab-subtitle">{category}</span>}
      </div>
      <div className={`tab-item ${activeTab === 'manufacturer' ? 'active' : ''}`} onClick={() => handleTabClick('manufacturer')}>
        Manufacturer {manufacturer && <span className="tab-subtitle">{manufacturer}</span>}
      </div>
      <div className={`tab-item ${activeTab === 'series' ? 'active' : ''}`} onClick={() => handleTabClick('series')}>
        Series {series && <span className="tab-subtitle">{series}</span>}
      </div>
      <div className={`tab-item ${activeTab === 'model' ? 'active' : ''}`} onClick={() => handleTabClick('model')}>
        Model {model && <span className="tab-subtitle">{model}</span>}
      </div>
      <div className={`tab-item ${activeTab === 'alternative' ? 'active' : ''}`} onClick={() => handleTabClick('alternative')}>
        Alternative
      </div>
    </div>
  );
};

export default TabNav;
