import React from 'react';
import { IS_LOCAL } from '../../config';
import './ItemCard.css';

const ItemCard = ({ item, onSelect }) => {
  const iconSrc = IS_LOCAL ? item.icon_local || item.logo_local : item.icon || item.logo;

  return (
    <div className='col-12 col-md-6 col-lg-4 m-0 p-2'>
      <div className={`card item ${item.code}`} onClick={() => onSelect(item)}>
        <div className="row g-0">
          {iconSrc && (
            <div className="col-2 d-flex justify-content-center align-items-center p-3">
              <img src={iconSrc} alt={item.name} className="icon-img" />
            </div>
          )}
          <div className={`col-${iconSrc ? '10' : '12'} d-flex align-items-center`}>
            <div className="ps-0 card-body">
              <h5 className="card-title m-0">{item.name}</h5>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ItemCard;
