import React from 'react';
import { useParams } from 'react-router-dom';
import modelsData from './models.json';
import ModelsCard from './ModelsCard';
import './RelatedModels.css';

const RelatedModels = () => {
  const { modelId } = useParams();
  const selectedModel = modelsData.models.find(model => model.id === parseInt(modelId));

  if (!selectedModel) {
    return <div>Modelo no encontrado.</div>;
  }

  const { good, better, best } = selectedModel.relatedModels;
  const relatedModels = [good, better, best].map(id => modelsData.models.find(model => model.id === id));

  return (
    <div className="related-models-container">
      <h2>Modelos relacionados</h2>
      <div className="grid-container">
        {relatedModels.map((model, index) => (
          <ModelsCard key={model.id} model={model} />
        ))}
      </div>
    </div>
  );
};

export default RelatedModels;
